package com.main.intoduction;

public class ArithmeticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=5;
		System.out.println("Addition of "+x+"and "+y+" is = "+(x+y));//15
		System.out.println("Subtraction of "+x+"and "+y+" is = "+(x-y));//5
		System.out.println("Multiplication of "+x+"and "+y+" is = "+(x*y));//50
		System.out.println("Division of "+x+"and "+y+" is = "+x/y);//2
		System.out.println("Remainder of "+x+"and "+y+" is = "+x%y);//0
	}

}
