package com.main.intoduction;

public class PrimitiveDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte x=10;
		short y=20;
		int z=44;
		long a=45;
		float f=23.5f;
		double d=545.55;
		char c='y';
		boolean b=false;
		System.out.println("--------Primitive datatypes--------");
		System.out.println("byte datatype   ="+x);
		System.out.println("short datatyp   =e"+y);
		System.out.println("int datatype    ="+z);
		System.out.println("long datatype   ="+a);
		System.out.println("float datatype  ="+f);
		System.out.println("double datatype ="+d);
		System.out.println("character datatype ="+c);
		System.out.println("boolean datatype ="+b+"\n\n");
		
		x=99;
		y=88;
		z=54;
		a=543;
		f=555.77f;
		d=444.888;
		c='a';
		b=true;
		
		System.out.println("After modifyng the values of datatypes;");
		System.out.println("byte datatype   ="+x);
		System.out.println("short datatyp   =e"+y);
		System.out.println("int datatype    ="+z);
		System.out.println("long datatype   ="+a);
		System.out.println("float datatype  ="+f);
		System.out.println("double datatype ="+d);
		System.out.println("character datatype= "+c);
		System.out.println("boolean datatype  ="+b);

	}

}
